import React, { useEffect, useState } from "react";
import { reportAPI, productsAPI, locationsAPI } from "../api";
import { Table, Button } from "react-bootstrap";

export default function BalanceReport() {
  const [rows, setRows] = useState([]);
  const [products, setProducts] = useState([]);
  const [locations, setLocations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => { load(); }, []);

  async function load() {
    setLoading(true);
    setError(null);
    try {
      const [prods, locs, balances] = await Promise.all([
        productsAPI.list(),
        locationsAPI.list(),
        reportAPI.balance()
      ]);

      setProducts(prods || []);
      setLocations(locs || []);

      // Map balances by product-location for quick lookup (location_id may be null -> treat as "External")
      const balanceMap = {};
      (balances || []).forEach(b => {
        const locKey = b.location_id === null || b.location_id === "" ? "__EXTERNAL__" : b.location_id;
        balanceMap[`${b.product_id}-${locKey}`] = b.qty;
      });

      // Build rows: for each product, each location, plus an "External" row if there are balances with external
      const allRows = [];

      // if no products or no locations, still show external balances if present
      (prods || []).forEach(p => {
        // per-location rows
        (locs || []).forEach(l => {
          const qty = balanceMap[`${p.product_id}-${l.location_id}`] || 0;
          allRows.push({
            product_id: p.product_id,
            location_id: l.location_id,
            qty
          });
        });

        // external row for this product if exists in balanceMap (or optionally always show)
        const externalQty = balanceMap[`${p.product_id}-$__EXTERNAL__$`.replace('$__EXTERNAL__$','__EXTERNAL__')] || balanceMap[`${p.product_id}-__EXTERNAL__`] || 0;
        // show external row only if qty > 0 OR if you want to always display external row, remove the qty check
        if (externalQty !== undefined) {
          // If you prefer to always show external row even when 0, push unconditionally.
          // Here we push if externalQty > 0 or to keep consistent with locations we push always:
          allRows.push({
            product_id: p.product_id,
            location_id: null, // treat as External
            qty: externalQty
          });
        }
      });

      // If there are no products but balances exist, create rows from balances directly
      if ((prods || []).length === 0 && (balances || []).length > 0) {
        (balances || []).forEach(b => {
          allRows.push({
            product_id: b.product_id,
            location_id: b.location_id,
            qty: b.qty
          });
        });
      }

      setRows(allRows);
    } catch (err) {
      console.error("BalanceReport load error:", err);
      setError("Failed to load balance report");
      setRows([]);
    } finally {
      setLoading(false);
    }
  }

  const getProductName = (id) => products.find(p => String(p.product_id) === String(id))?.name || id;
  const getLocationName = (id) => {
    if (id === null || id === "" || id === undefined) return "External";
    return locations.find(l => String(l.location_id) === String(id))?.name || id;
  };

  return (
    <div style={{ backgroundColor: "#3d0075", minHeight: "100vh", padding: "20px", color: "white", borderRadius: "8px" }}>
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h2 className="mb-0">Balance Report (Product / Warehouse / Qty)</h2>
        <Button variant="secondary" className="ms-2" onClick={load} style={{ backgroundColor: "#6a0dad", border: "none" }}>
          Refresh
        </Button>
      </div>

      {loading && <div className="small-muted mb-3">Loading...</div>}
      {error && <div className="text-danger mb-3">{error}</div>}

      <Table striped bordered hover responsive style={{ backgroundColor: "#f8f8ff", color: "black" }}>
        <thead>
          <tr><th>Product</th><th>Warehouse</th><th>Qty</th></tr>
        </thead>
        <tbody>
          {(!loading && rows.length === 0) && (
            <tr>
              <td colSpan="3" className="text-center small-muted p-4">No data</td>
            </tr>
          )}

          {rows.map((r, idx) => (
            <tr key={`${r.product_id}-${r.location_id ?? 'external'}-${idx}`}>
              <td>{getProductName(r.product_id)}</td>
              <td>{getLocationName(r.location_id)}</td>
              <td>{r.qty}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}
